<?php	require_once('../hidden/helper_classes/NP_RequestHandler.php');	require_once('../hidden/service_layer/NP_AuthenticationService.php');		class AuthenticationHandler extends RequestHandler	{		function handlePut($token) {						//Get the JSON from the request			$request = SupportingMethods::getValidJSON();						$authService = new AuthenticationService($this->DBH);			if (array_key_exists ('facebook_token', $request )){				$this->response->records[] = $authService->authenticateByFacebookToken($request['facebook_token']);			}			else				throw new RequiredParameterMissingException(null);		}	}		$handler = new AuthenticationHandler;	$handler->handleRequest();?>