<?php	require_once('../hidden/helper_classes/NP_RequestHandler.php');	require_once('../hidden/service_layer/NP_WorkoutService.php');	class WorkoutHandler extends RequestHandler	{		function handlePost($token) { $this->handleSimplePost($token); }		function handlePut($token) { $this->handleSimplePut($token); }		function handleGet($token) { 						$workoutService = new WorkoutService($this->DBH);			if(array_key_exists('request_type', $_GET) && $_GET['request_type'] == 'upcoming')				$this->response->records = $workoutService->getUpcomingWorkouts(array_key_exists('tribe_id', $_GET) ? $_GET['tribe_id'] : null);			else				$this->handleSimpleGet($token);		}	}		$handler = new WorkoutHandler;	$handler->handleRequest();?>