<?php	require_once('../hidden/helper_classes/NP_RequestHandler.php');	require_once('../hidden/service_layer/NP_WorkoutLocationService.php');		class WorkoutLocationHandler extends RequestHandler	{		function handlePost() { $this->handleSimplePost(); }		function handlePut() { $this->handleSimplePut(); }		function handleGet()		{			$workout_location_service = new WorkoutLocationService($this->DBH);						if(!empty($_GET['id']))				$this->response->records = $workout_location_service->getWorkoutLocationById($_GET['id']);			elseif(!empty($_GET['tribe_id']))				$this->response->records = $workout_location_service->getWorkoutLocationByTribeId($_GET['tribe_id']);			else				$this->response->records = $workout_location_service->getAllWorkoutLocations();		}	}		$handler = new WorkoutLocationHandler;	$handler->handleRequest();?>